import React, { Component } from 'react';
import { Table } from 'react-bootstrap';

class ShowUser extends Component {
  state = {
    response: '',
    name: '',
    email: '',
    age: '',
    responseToPost: '',
  };
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      users: []
    }
  }

  componentDidMount() {
    fetch('/users/')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            users: result
          });          
        },
        (error) => {
          this.setState({ error });
        }
      )
  }  

render() {
    const { error, users} = this.state;    
    if(error) {
      return (
        <div>Error: {error.message}</div>
      )
    } else {
      return(
        <div>
          <h2>Users List</h2>
          <Table>
            <thead>
              <tr>
                <th>#ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>Age</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>                  
                  <td>{user.email}</td>                  
                  <td>{user.age}</td>                  
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      )
    }
  }
}

export default ShowUser;
